//funtion for display list
let ullist = document.querySelector(".form-left-content-box ul");
function displayList(elemant, object) {
  const displayObject = object
    .map((p) => {
      return `<li data-id="${p.word}" class="${object.length}"> ${p.word}</li>`;
    })
    .join("");
  elemant.innerHTML = displayObject;
}

displayList(ullist, object);

// function for display definstion
function displayDefinistion(elemant, object) {
  const displayObject = object
    .map((p) => {
      return `
        <h2 id="data-word">${p.word}</h2>
        <div class="definition" id="data-des">
            ${p.definition}
        </div>
        `;
    })
    .join("");
  elemant.innerHTML = displayObject;
  var dataDes = document.querySelector('#data-des');
  var sceen_height = document.body.offsetHeight;
  var header = document.querySelector('header').offsetHeight;
  var rightIntorHeight = document.querySelector('.form-right-intro').offsetHeight;
  var dataWord = document.querySelector('#data-word').offsetHeight;
  var IloveHeight = document.querySelector('.Ilove').offsetHeight;
  var footerHieght = document.querySelector('#footer').offsetHeight;
  var getHeight = header + rightIntorHeight + dataWord +IloveHeight + footerHieght + 31; 
  dataDes.style.height = sceen_height - getHeight + 'px';
}

