//search list item the main input
let input = document.getElementById("input-item");
let searchBtn = document.getElementById("search-btn");
let searchVoiceBtn = document.getElementById("search-voice-btn");
function search() {
  for (let i = 0; i < list.length; i += 1) {
    if (list[i].innerText.toLowerCase().includes(input.value.toLowerCase())) {
      list[i].style.display = "block";
    } else {
      list[i].style.display = "none";
    }
  }
}
// search with voice
function handleVoiceSearch() {
  const recognition = new webkitSpeechRecognition();
  recognition.lang = "km-KH";
  searchVoiceBtn.classList.add("search-voice");
  recognition.onresult = (event) => {
    const result = event.results[0][0].transcript;
    input.value = result;
    search();
    searchVoiceBtn.classList.remove("search-voice");
  };
  recognition.start();
}

searchVoiceBtn.addEventListener("mousedown", handleVoiceSearch);
input.addEventListener("input", search);

// save to locastorage when user click on button save love
const inputVal = document.getElementsByClassName("input")[0];
const addTaskBtn = document.getElementsByClassName("save")[0];
addTaskBtn.addEventListener("click", function () {
  if (inputVal.value.trim() != "") {
    let localItems = JSON.parse(localStorage.getItem("localItem"));
    if (localItems === null) {
      taskList = [];
    } else {
      taskList = localItems;
    }
    // Check if the item already exists in the list
    if (taskList.includes(inputVal.value)) {
      taskList.splice(taskList.indexOf(inputVal.value), 1);
      addTaskBtn.classList.remove("red");
    } else {
      taskList.push(inputVal.value);
      addTaskBtn.classList.add("red");
    }
    localStorage.setItem("localItem", JSON.stringify(taskList));
  }
  showItem();
});
function showItem() {
  let localItems = JSON.parse(localStorage.getItem("localItem"));
  if (localItems === null) {
    taskList = [];
  } else {
    taskList = localItems;
  }
  let html = "";
  let itemShow = document.querySelector(".historyUl");
  taskList.forEach((data, index) => {
    html += `
      <li data-id="${data}">${data}
        <button class="deleteTask" onClick="deleteItem(${index})">
          <i class="fa-solid fa-xmark"></i>
        </button>
      </li>
    `;
  });
  itemShow.innerHTML = html;
}
showItem();
// Clear one list
function deleteItem(index) {
  let localItems = JSON.parse(localStorage.getItem("localItem"));
  taskList.splice(index, 1);
  localStorage.setItem("localItem", JSON.stringify(taskList));
  showItem();
}

// for user stor when user input
const inputItem = document.getElementsByClassName("input-item")[0];
inputItem.addEventListener("change", function () {
  let inputVal = inputItem.value.trim();
  if (inputVal !== "") {
    let mytaskList = JSON.parse(localStorage.getItem("myItem"));
    if (mytaskList === null) {
      mytaskList = [];
    }
    mytaskList.push(inputVal);
    localStorage.setItem("myItem", JSON.stringify(mytaskList));
  }
  dispayItem();
});

function dispayItem() {
  let mytaskList = JSON.parse(localStorage.getItem("myItem"));
  if (mytaskList === null) {
    mytaskList = [];
  }
  let html = "";
  let itemShow = document.querySelector(".historyUl1");
  mytaskList.forEach((data, index) => {
    html += `
      <li data-id="${data}">${data}
        <button class="deleteTask" onClick="deleteMyItem(${index})">
          <i class="fa-solid fa-xmark"></i>
        </button>
      </li>
    `;
  });
  itemShow.innerHTML = html;
}

// Clear one list
function deleteMyItem(index) {
  let mytaskList = JSON.parse(localStorage.getItem("myItem"));
  mytaskList.splice(index, 1);
  localStorage.setItem("myItem", JSON.stringify(mytaskList));
  dispayItem();
}

dispayItem();

//function for check value if input value have text like list
function checkInputValue() {
  const inputValue = inputVal.value.trim();
  let myList = document.querySelectorAll(".historyUl li");
  let listMatch = false;
  myList.forEach((item) => {
    if (item.dataset.id === inputValue) {
      // Use dataset.id instead of value
      listMatch = true;
    }
  });
  if (listMatch) {
    addTaskBtn.classList.add("red");
  } else {
    addTaskBtn.classList.remove("red");
  }
}
inputVal.addEventListener("input", checkInputValue);

